"use client";

import React, { useState, useEffect } from "react";
import { useStream } from "@langchain/langgraph-react";

export default function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  // LangGraph streaming hook
  const { stream, send } = useStream({
    apiUrl: "http://localhost:8000/graph", // Your FastAPI + LangGraph endpoint
    graphId: "agent", // Replace with your graph ID
  });

  // Listen for AI responses
  useEffect(() => {
    if (!stream) return;

    const onData = (event) => {
      if (event.type === "content") {
        setMessages((prev) => [...prev, { role: "ai", content: event.content }]);
      }
    };

    stream.on("data", onData);
    return () => {
      stream.off("data", onData);
    };
  }, [stream]);

  const handleSend = () => {
    if (!input.trim()) return;
    setMessages((prev) => [...prev, { role: "user", content: input }]);
    send({ input });
    setInput("");
  };

  return (
    <div className="flex flex-col h-screen max-w-2xl mx-auto p-4 bg-white shadow-lg rounded-2xl">
      {/* Chat area */}
      <div className="flex-1 overflow-y-auto p-3 space-y-3 bg-gray-50 rounded-lg border">
        {messages.map((msg, i) => (
          <div
            key={i}
            className={`max-w-[80%] p-3 rounded-2xl text-sm ${
              msg.role === "user"
                ? "bg-blue-600 text-white ml-auto rounded-br-none"
                : "bg-gray-200 text-gray-900 mr-auto rounded-bl-none"
            }`}
          >
            {msg.content}
          </div>
        ))}
      </div>

      {/* Input bar */}
      <div className="flex mt-3">
        <input
          type="text"
          className="flex-1 border p-3 rounded-l-2xl outline-none"
          placeholder="Type your message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && handleSend()}
        />
        <button
          onClick={handleSend}
          className="bg-blue-600 text-white px-6 rounded-r-2xl hover:bg-blue-700"
        >
          Send
        </button>
      </div>
    </div>
  );
}
