"use client";

import { useStream } from "@langchain/langgraph-sdk/react";
import { useRef } from "react";
import "./App.css";

export default function App() {
  const fileInputRef = useRef(null);

  const thread = useStream({
    apiUrl: "http://127.0.0.1:2024",
    assistantId: "agent",
    messagesKey: "messages",
  });

  const handleFileUpload = async (event) => {
    const file = event.target.files?.[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("file", file);

    // // üîÅ Send to your backend file upload endpoint
    // const res = await fetch("http://127.0.0.1:2024/upload", {
    //   method: "POST",
    //   body: formData,
    // });

    if (res.ok) {
      thread.submit({
        messages: [
          {
            type: "system",
            content: `üìé Uploaded document: ${file.name}`,
          },
        ],
      });
    }
  };

  return (
    <div className="chat-container">
      {/* Messages */}
      <div className="messages">
        {thread.messages.map((msg) => (
          <div
            key={msg.id}
            className={`message ${msg.role === "human" ? "user" : "bot"}`}
          >
            {msg.content}
          </div>
        ))}
      </div>

      {/* Input bar */}
      <form
        className="input-bar"
        onSubmit={(e) => {
          e.preventDefault();
          const form = e.target;
          const message = new FormData(form).get("message");
          form.reset();
          thread.submit({ messages: [{ type: "human", content: message }] });
        }}
      >
        {/* Upload Button */}
        {/* <label className="upload-button" title="Upload PDF">
          üìé
          <input
            type="file"
            accept=".pdf"
            ref={fileInputRef}
            onChange={handleFileUpload}
            hidden
          />
        </label> */}

        {/* Input */}
        <input
          type="text"
          name="message"
          placeholder="Ask a question..."
          autoComplete="off"
        />

        {/* Send/Stop Button */}
        {thread.isLoading ? (
          <button type="button" onClick={() => thread.stop()}>
            Stop
          </button>
        ) : (
          <button type="submit">Send</button>
        )}
      </form>
    </div>
  );
}
