"use client";

import { useStream } from "@langchain/langgraph-sdk/react";

import "./App.css";
import ReactMarkdown from 'react-markdown';
import { useEffect, useState } from "react";


{/* <ReactMarkdown>{report}</ReactMarkdown> */}


export default function App() {
  const [custom ,setCustom] =useState([])

  useEffect(() => {
console.log(custom)
}, [custom]);

//  console.log('refreshed')
  const thread = useStream({
    apiUrl: "http://127.0.0.1:9090",
    // apiUrl: "http://127.0.0.1:2023",
    assistantId: "agent",
    messagesKey: "messages",
    onFinish:(finalevent)=>{setFinalMessages(thread.messages); 
      setPartialResponse(""); // clear partial
      },
    onCustomEvent: (event, options) => {setCustom(event)}

    // onCustomEvent: for custom event handler

  });

// useEffect(()=>{thread.events.forEach(element => {

// });((e)=>{console.log(e)})})

  return (
    <div className="chat-container">

      <div className="messages">
        {custom?custom:thread.messages.map((msg) => (
          <div
            key={msg.id}
            className={`message ${msg.role === "human" ? "user" : "bot"}`}
          >
            <ReactMarkdown>{msg.content}</ReactMarkdown>
          </div>
        ))}
      </div>

      {/* Input bar */}
      <form
        className="input-bar"
        onSubmit={(e) => {
          e.preventDefault();
          const form = e.target;
          const message = new FormData(form).get("message");
          form.reset();

          thread.submit({
          task: message,          
          revision_number: 1,
          max_revisions: 2,
          },{streamMode:["custom"]});

        }}
      >


        {/* Input */}
        <input
          type="text"
          name="message"
          placeholder="Ask a question..."
          autoComplete="off"
        />

        {/* Send/Stop Button */}
        {thread.isLoading ? (
          <button type="button" onClick={() => thread.stop()}>
            Stop
          </button>
        ) : (
          <button type="submit">Send</button>
        )}
      </form>
    </div>
  );
}
